
function PlaylistModel(){var self=this;self.settings=ko.observableArray([{duration_limit:600}]);self.videos=ko.observableArray([]);self.users=ko.observableArray([]);self.messages=ko.observableArray([]);self.addVideo=function(model,item){video=$(item.currentTarget);videoarray={"title":video.attr('data-title'),"image":video.attr('data-image'),"id":video.attr('data-id')};var request=gapi.client.youtube.videos.list({part:'contentDetails',id:videoarray.id,type:'video',}); request.execute(function(response){videoarray.duration=response.items[0].contentDetails.duration;$.ajax({type:"GET",url:"/playlist_add_video",dataType:"json",data:videoarray,}).done(function(data){self.sync();});});$("#response").hide();$("#search").val("");};self.getUsersTotal=function(){return self.users().length;};self.getSetting=function(key){if(self.settings()[key])
return self.settings()[key];else
return false;};self.removeFirstVideo=function(model,item){video=self.videos()[0].id();$.ajax({type:"GET",url:"/remove_video",dataType:"json",data:{video_id:video},}).done(function(data){self.sync();});};self.sync=function(){$.ajax({type:"GET",url:"/playlist_videos",dataType:"json",}).done(function(data){var mappedVideos=$.map(data.videos,function(item){return new Video(item);});self.videos(mappedVideos);self.settings(data.settings);var mappedUsers=$.map(data.users,function(item){return new User(item);});self.users(mappedUsers);var mappedMessages=$.map(data.messages,function(item){return new Message(item);});self.messages(mappedMessages);});};self.syncInterval=function(period){self.sync();setTimeout(function(){self.syncInterval(period);},period);};self.syncInterval(2000);self.firstVideoTitle=ko.computed(function(){if(self.videos()[0]){return self.videos()[0].title();}
else{return"Not yet chosen.";}},self);self.firstVideoId=ko.computed(function(){if(self.videos()[0]){return self.videos()[0].id();}
else{return false;}},self);self.firstVideoStart=ko.computed(function(){if(self.videos()[0]){return parseInt(self.videos()[0].start_time());}
else
return 0;},self);}
function Video(data){this.title=ko.observable(data.title);this.id=ko.observable(data.id);this.image=ko.observable(data.image);this.likes=ko.observable(data.likes);this.liked=ko.observable(data.liked);this.owner=ko.observable(data.owner);this.start_time=ko.observable(data.start_time);this.addLike=function(){$.ajax({type:"GET",url:"/like_video",dataType:"json",data:{"video_id":this.id,"like":1
},}).done(function(data){playlist.sync();});};this.removeLike=function(){$.ajax({type:"GET",url:"/like_video",dataType:"json",data:{"video_id":this.id,"like":-1
},}).done(function(data){playlist.sync();});};this.removeVideo=function(model,item){$.ajax({type:"GET",url:"/remove_video",dataType:"json",data:{"video_id":this.id},}).done(function(data){playlist.sync();});$("#response").hide();$("#search").val("");};}
function User(data){this.username=ko.observable(data.username);this.grantRights=function(model,item){$.ajax({type:"GET",url:"/grant_rights",dataType:"json",data:{"user_id":this.id},}).done(function(data){playlist.sync();});};}
function Message(data){this.author=ko.observable(data.author);this.time=ko.observable(data.time);this.message=ko.observable(data.message);}
playlist=new PlaylistModel();ko.applyBindings(playlist);